function firstLoad(){initVars(),showMenu(),stickyNav(),initPlaceholders()}function initVars(){$mainNav=$("nav"),$showMenu=$("a#showMenu, nav li a")}function showMenu(){$showMenu.click(function(){return $mainNav.hasClass("expand")?menuOut():menuIn(),!1})}function menuOut(){$mainNav.removeClass("expand")}function menuIn(){$mainNav.addClass("expand")}function initPlaceholders(){$("input[placeholder]").length>0&&(placeholderSupported()||($("input[placeholder]").focus(function(){var n=$(this);n.val()==n.attr("placeholder")&&(n.val(""),n.removeClass("placeholder"))}).blur(function(){var n=$(this);(""==n.val()||n.val()==n.attr("placeholder"))&&(n.addClass("placeholder"),n.val(n.attr("placeholder")))}).blur(),$("input[placeholder]").parents("form").submit(function(){$(this).find("[placeholder]").each(function(){var n=$(this);n.val()==n.attr("placeholder")&&n.val("")})})))}function placeholderSupported(){return test=document.createElement("input"),"placeholder"in test}function stickyNav(){$("nav").scrollspy({min:$("#about").offset().top,max:$("footer").offset().top,container:window,onEnter:function(){$("nav").addClass("fixed")},onLeave:function(){$("nav").removeClass("fixed")}})}var $showMenu,$header,$mainNav,$content;$(function(){firstLoad()});