$(function(){$("#top-link, #about-link, #portfolio-link, #clients-link, #services-link, #team-link, #contact-link, #slider-services-link, #slider-services-link2, #slider-about-link").click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var i=$(this.hash);if(i=i.length?i:$("[name="+this.hash.slice(1)+"]"),i.length)return $("html,body").animate({scrollTop:i.offset().top+2},1e3),!1}})});